function categorizeArticle(title, description, context) {
  const textToAnalyze = `${title} ${description} ${context}`.toLowerCase();

  // First, look for explicit category markers
  const explicitCategoryPattern = /\[(\w+\s+\d+)\s*\|\s*([^\]]+)\]/;
  const categoryMatch = context.match(explicitCategoryPattern);
  if (categoryMatch) {
    const foundCategory = categoryMatch[2].trim();
    const normalizedCategory = categoryMapping[foundCategory.toLowerCase()] || foundCategory;
    return normalizedCategory;
  }

  // Score each category based on keyword matches
  const categoryScores = {};
  for (const [category, keywords] of Object.entries(categoryKeywords)) {
    let score = 0;
    for (const keyword of keywords) {
      const regex = new RegExp(`\\b${keyword.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}\\b`, 'gi');
      const matches = textToAnalyze.match(regex);
      if (matches) {
        score += matches.length;
        // Bonus for title matches
        if (title.toLowerCase().includes(keyword)) {
          score += 2;
        }
      }
    }
    categoryScores[category] = score;
  }

  // Find the category with the highest score
  const maxScore = Math.max(...Object.values(categoryScores));
  if (maxScore > 0) {
    const bestCategory = Object.keys(categoryScores).find(cat => categoryScores[cat] === maxScore);
    return bestCategory;
  }

  // Default fallback
  return 'Tech';
}

const items = [];

if (!htmlText || htmlText.length < 100) {
  console.log('ERROR: No valid HTML data');
  items.push({
    title: 'No HTML Data',
    description: 'Failed to retrieve HTML content from tldr.tech',
    link: 'https://tldr.tech/',
    pubDate: new Date().toISOString(),
    category: 'Error'
  });
} else {
  try {
    console.log('=== PARSING TLDR ARTICLES ===');

    // Enhanced parsing - look for section headings and articles within sections
    const sectionPattern = /<h[1-6][^>]*>([^<]+)<\/h[1-6]>/gi;
    const sectionMatches = htmlText.match(sectionPattern);

    console.log('Section headings found:', sectionMatches ? sectionMatches.length : 0);

    // Look for article containers - TLDR typically uses structured divs
    const articleContainerPatterns = [
      /<article[^>]*>([\s\S]*?)<\/article>/gi,
      /<div[^>]*class="[^"]*article[^"]*"[^>]*>([\s\S]*?)<\/div>/gi,
      /<div[^>]*class="[^"]*story[^"]*"[^>]*>([\s\S]*?)<\/div>/gi
    ];

    let articlesFound = false;

    for (const pattern of articleContainerPatterns) {
      const containerMatches = htmlText.match(pattern);
      if (containerMatches && containerMatches.length > 0) {
        console.log(`Found ${containerMatches.length} article containers with pattern`);
        articlesFound = true;

        containerMatches.forEach((containerHtml, index) => {
          try {
            // Extract link
            const linkMatch = containerHtml.match(/<a[^>]*href="([^"]+)"[^>]*>([\s\S]*?)<\/a>/);

            if (linkMatch) {
              const href = linkMatch[1];
              const linkContent = linkMatch[2];

              // Skip if not an external article link
              if (!href.startsWith('http') || href.includes('tldr.tech') || href.includes('unsubscribe') || href.includes('mailto')) {
                return;
              }

              // Extract title
              let title = linkContent.replace(/<[^>]*>/g, '').trim();

              // Clean up title - remove read time indicators
              title = title.replace(/\(\d+\s*min(?:ute)?\s*read\)/gi, '').trim();

              // Skip if title is too short or looks like navigation
              if (title.length < 10 || 
                  /^(home|about|contact|login|sign|menu|nav|footer|header|search|subscribe|privacy|terms|cookies?|more|read|click|here|unsubscribe)$/i.test(title)) {
                return;
              }

              // Get surrounding context for better categorization
              const contextStart = Math.max(0, htmlText.indexOf(containerHtml) - 500);
              const contextEnd = Math.min(htmlText.length, htmlText.indexOf(containerHtml) + containerHtml.length + 500);
              const context = htmlText.substring(contextStart, contextEnd);

              // Extract description from container
              let description = containerHtml.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();
              description = description.replace(title, '').trim();

              // Limit description length
              if (description.length > 200) {
                description = description.substring(0, 200) + '...';
              }

              // Enhanced categorization
              const category = categorizeArticle(title, description, context);

              // Extract date
              let pubDate = new Date();
              const datePatterns = [
                /\[(\w+\s+\d+)\s*\|/,
                /(\w+\s+\d+,?\s*\d{4})/,
                /(\d{1,2}\/\d{1,2}\/\d{4})/
              ];

              for (const datePattern of datePatterns) {
                const dateMatch = context.match(datePattern);
                if (dateMatch) {
                  try {
                    const dateStr = dateMatch[1];
                    const parsedDate = new Date(dateStr.includes('2024') || dateStr.includes('2025') ? dateStr : `${dateStr} ${new Date().getFullYear()}`);
                    if (!isNaN(parsedDate.getTime())) {
                      pubDate = parsedDate;
                      break;
                    }
                  } catch (e) {
                    // Continue with next pattern
                  }
                }
              }

              // Determine source from URL
              let source = 'tldr';
              try {
                const urlObj = new URL(href);
                const domain = urlObj.hostname.replace('www.', '').replace(/\./g, '');
                source = domain || 'tldr';
              } catch (e) {
                source = 'tldr';
              }

              const article = {
                title: title,
                description: `${category}: ${title}${description ? ' - ' + description : ''}`,
                link: href,
                pubDate: pubDate.toISOString(),
                category: category,
                source: source,
                section: 'TLDR',
                confidence: 'high' // Mark as high confidence due to enhanced parsing
              };

              items.push(article);
              console.log(`Added TLDR article ${items.length}: ${title.substring(0, 50)} [${category}]`);
            }

          } catch (error) {
            console.log(`Error processing article container ${index}:`, error.message);
          }
        });

        if (items.length > 0) break; // Stop after first successful pattern
      }
    }

    // Fallback to original link-based parsing if no articles found
    if (!articlesFound) {
      console.log('Using fallback link-based parsing');
      const linkPattern = /<a[^>]*href="([^"]+)"[^>]*>([\s\S]*?)<\/a>/gi;
      const linkMatches = htmlText.match(linkPattern);

      if (linkMatches && linkMatches.length > 0) {
        linkMatches.forEach((linkHtml, index) => {
          try {
            const linkMatch = linkHtml.match(/<a[^>]*href="([^"]+)"[^>]*>([\s\S]*?)<\/a>/);

