              }

              const contextStart = Math.max(0, htmlText.indexOf(linkHtml) - 300);
              const contextEnd = Math.min(htmlText.length, htmlText.indexOf(linkHtml) + linkHtml.length + 300);
              const context = htmlText.substring(contextStart, contextEnd);

              const category = categorizeArticle(title, '', context);

              let source = 'tldr';
