return items.map(item => {
  let parsed;
  let outputText = item.json.output || '';

  // Handle case where output might be wrapped in markdown code blocks
  let jsonText = outputText.replace(/^```json\n?/, '').replace(/\n?```$/, '').trim();

  try {
    parsed = JSON.parse(jsonText);
    return { 
      json: {
        action: parsed.action || 'UNKNOWN',
        agent: parsed.agent || 'UNKNOWN'
      }
    };
  } catch (e) {
    // Try to extract JSON from the text using regex
    const jsonMatch = outputText.match(/{[\s\S]*?}/);
    if (jsonMatch) {
      try {
        parsed = JSON.parse(jsonMatch[0]);
        return { 
          json: {
            action: parsed.action || 'UNKNOWN',
            agent: parsed.agent || 'UNKNOWN'
          }
        };
      } catch (e2) {
        // Fallback: try to extract values manually
        const actionMatch = outputText.match(/"action"\s*:\s*"([^"]*)"/);
        const agentMatch = outputText.match(/"agent"\s*:\s*"([^"]*)"/);

        if (actionMatch && agentMatch) {
          return { 
            json: {
              action: actionMatch[1],
              agent: agentMatch[1]
            }
          };
        }
      }
    }

    // Final fallback
    console.log('Failed to parse JSON from output:', outputText);
    return { 
      json: {
        action: "UNKNOWN",
        agent: "Chat Agent"
      }
    };
  }
});

