try {
  const inputData = $input.all();

  if (!inputData || inputData.length === 0) {
    return [{
      json: {
        user_message: "No file changes found in the pull requests.",
        error: "Empty input data"
      }
    }];
  }

  const files = inputData.map(item => item.json).filter(file => file && typeof file === 'object');

  if (files.length === 0) {
    return [{
      json: {
        user_message: "No valid file data found in the pull requests.",
        error: "No valid files"
      }
    }];
  }

  let diffs = '';
  let currentPR = null;
  let prCount = 0;

  // Group files by PR
  const prGroups = {};
  files.forEach(file => {
    const prKey = `${file.repository}#${file.prNumber}`;
    if (!prGroups[prKey]) {
      prGroups[prKey] = {
        repository: file.repository,
        prNumber: file.prNumber,
        prTitle: file.prTitle,
        prUrl: file.prUrl,
        files: []
      };
    }
    prGroups[prKey].files.push(file);
  });

  // Process each PR group
  Object.keys(prGroups).forEach(prKey => {
    const pr = prGroups[prKey];
    prCount++;

    diffs += `\n## PR ${prCount}: ${pr.repository} #${pr.prNumber}\n`;
    diffs += `**Title:** ${pr.prTitle}\n`;
    diffs += `**URL:** ${pr.prUrl}\n\n`;

    for (const file of pr.files) {
      if (!file.filename) continue;

      diffs += `### File: ${file.filename}\n`;
      diffs += `**Status:** ${file.status || 'unknown'} | **Changes:** +${file.additions || 0}/-${file.deletions || 0}\n\n`;

      if (file.patch) {
        // Replace triple backticks to avoid markdown conflicts
        const safePatch = file.patch.replace(/```/g, "''");

        diffs += "```diff\n";
        diffs += safePatch;
        diffs += "\n```\n";
      } else {
        diffs += "_No patch available (likely binary file or too large)._";
      }

      diffs += "\n---\n\n";
    }

    diffs += "\n===========================\n\n";
  });

  if (!diffs.trim()) {
    return [{
      json: {
        user_message: "No file diffs found to review.",
        error: "No diffs available"
      }
    }];
  }

  const userMessage = `Please review the following code changes across ${prCount} pull request(s):\n\n${diffs}`;

  return [
    {
      json: {
        user_message: userMessage.trim(),
        totalPRs: prCount,
        totalFiles: files.length
      }
    }
  ];
} catch (error) {
  return [{
    json: {
      user_message: "Error processing pull request files: " + error.message,
      error: error.message
    }
  }];
}

