}

console.log(`Created ${results.length} items for Tardigrade PR processing`);
return results;

// Parse the JSON output from the PowerShell script and split repos that need updates
const inputData = $input.first();
const stdout = inputData.json.stdout || '';

try {
  // Parse the JSON output from PowerShell
  const statusData = JSON.parse(stdout);
  const results = [];

  // Create separate items for repos that are behind (need updates)
  Object.keys(statusData.repositories).forEach(repoName => {
    const repo = statusData.repositories[repoName];
    if (repo.status === 'BEHIND') {
      results.push({
        json: {
          name: repoName,
          path: repo.path,
          status: repo.status,
          behind: repo.behind,
          ahead: repo.ahead,
          lastCommit: repo.lastCommit,
          needsUpdate: true
        }
      });
    }
  });

  // If no repos need updates, return a single status summary
  if (results.length === 0) {
    return [{
      json: {
        timestamp: statusData.timestamp,
        summary: statusData.summary,
        message: 'All repositories are up to date',
        needsUpdate: false
      }
    }];
  }

  return results;
} catch (error) {
  // Fallback if JSON parsing fails
  return [{
    json: {
      timestamp: new Date().toISOString(),
      error: 'Failed to parse PowerShell output',
      rawOutput: stdout,
      parseError: error.message,
      needsUpdate: false
    }
  }];
}

// Process repos and split flow: pull updates OR go to summary
const inputData = $input.all();
console.log('Route for Updates input:', JSON.stringify(inputData, null, 2));

// Check if we have any repos that actually need updates
const reposWithUpdates = inputData.filter(item => item.json.needsUpdate === true);
console.log(`Found ${reposWithUpdates.length} repos with needsUpdate=true`);

const results = [];

if (reposWithUpdates.length > 0) {
  // We have repos that need updates - create pull commands for each
  console.log('Creating pull commands for repositories:');

  reposWithUpdates.forEach(repo => {
    console.log(`- Processing ${repo.json.name}: ${repo.json.behind} commits behind`);

    // Create pull command for this repo
    const pullCommand = `powershell -Command "git config --global --add safe.directory '${repo.json.path}'; cd '${repo.json.path}'; git fetch origin; if (-not (git branch --list main)) { git checkout -b main origin/main } else { git checkout main }; git pull origin main"`;

    results.push({
      json: {
        name: repo.json.name,
        path: repo.json.path,
        pullCommand: pullCommand,
        behind: repo.json.behind,
        status: 'PULLING_UPDATES'
      }
    });
  });

  console.log(`Created ${results.length} pull commands`);
  return results;
} else {
  // No repos need updates
  console.log('No repositories need updates - creating summary');
  return [{
    json: {
      message: 'All repositories are up to date',
      timestamp: new Date().toISOString(),
