const processedData = $input.first();
console.log('Add Contributors node - Input received:', JSON.stringify(processedData, null, 2));

// Handle multiple input formats from n8n
let categories, dateRange, gitContributors = [];
if (processedData && processedData.json) {
  categories = processedData.json.categories;
  dateRange = processedData.json.dateRange;
  gitContributors = processedData.json.contributors || [];
} else if (Array.isArray(processedData) && processedData.length > 0) {
  const firstItem = processedData[0];
  if (!firstItem || !firstItem.categories) {
    throw new Error('No categories data found in array input from Process & Categorize Commits node');
  }
  categories = firstItem.categories;
  dateRange = firstItem.dateRange;
  gitContributors = firstItem.contributors || [];
} else if (processedData && processedData.categories) {
  categories = processedData.categories;
  dateRange = processedData.dateRange;
  gitContributors = processedData.contributors || [];
} else {
  throw new Error('No categories data received from Process & Categorize Commits node. Input structure: ' + JSON.stringify(processedData));
}

console.log('Git contributors from Process Commits node:', gitContributors);

// Contributor mapping - map git usernames/names to proper display names
const contributorMapping = {
  // Sergiu Jomiru mapping (exact match)
  'Sergiu Jomiru': 'Sergiu Jomiru',

  // Ivan Lam mapping
  'ivan-lam': 'Ivan Lam',
  'ivanlam11703': 'Ivan Lam',

  // John Thompson mapping
  'john-lsas': 'John Thompson',
  'John Thompson': 'John Thompson'
};

// Function to map contributor names
const mapContributorName = (name) => {
  const trimmed = name.trim();
  // Try exact match first
  if (contributorMapping[trimmed]) {
    return contributorMapping[trimmed];
  }
  // Try case-insensitive match
  const lowerName = trimmed.toLowerCase();
  for (const [key, value] of Object.entries(contributorMapping)) {
    if (key.toLowerCase() === lowerName) {
      return value;
    }
  }
  // Return original name if no mapping found
  return trimmed;
};

// Use git contributors if available, otherwise fallback to default
let contributorsArray;
if (gitContributors && gitContributors.length > 0) {
  // Filter, clean, and map the git contributors
  const filteredContributors = gitContributors.filter(name => {
    const trimmed = name.trim();
    return trimmed.length > 1 && 
           trimmed.length < 50 &&
           !trimmed.toLowerCase().includes('bot') &&
           !trimmed.toLowerCase().includes('action') &&
           !trimmed.includes('noreply') &&
           !trimmed.includes('@');
  });

  // Map to proper display names and remove duplicates
  const mappedContributors = filteredContributors.map(mapContributorName);
  contributorsArray = [...new Set(mappedContributors)];
} else {
  // Fallback: try to extract from commit messages
  const contributorSet = new Set();

  Object.keys(categories).forEach(category => {
    if (categories[category] && Array.isArray(categories[category])) {
      categories[category].forEach(commit => {
        // Pattern 1: "by @username" or "by username"
        const byMatch = commit.match(/\bby\s+@?([a-zA-Z0-9_-]+)/i);
        if (byMatch) {
          contributorSet.add(mapContributorName(byMatch[1]));
        }

        // Pattern 2: "Co-authored-by: Name <email>"
        const coAuthorMatch = commit.match(/Co-authored-by:\s*([^<]+)/i);
        if (coAuthorMatch) {
          contributorSet.add(mapContributorName(coAuthorMatch[1].trim()));
        }
      });
    }
  });

  contributorsArray = [...new Set(Array.from(contributorSet).filter(name => name.length > 1))];
}

// If still no contributors found, use default list with proper names
if (contributorsArray.length === 0) {
  contributorsArray = ['Sergiu Jomiru', 'Ivan Lam', 'John Thompson'];
}

const contributors = contributorsArray.join(', ');

// Create final combined output
const finalOutput = {
  categories: categories,
  dateRange: dateRange,
  contributors: contributors
};

console.log('Add Contributors node - Final contributors:', contributorsArray);
console.log('Add Contributors node - Combined output:', JSON.stringify(finalOutput, null, 2));

return finalOutput;

const combinedInput = $input.first();
let stdout = combinedInput.json && combinedInput.json.stdout;

if (!stdout) {
  return [{ categories: { 'Added': [], 'Fixed': [], 'Changed': [], 'Removed': [], 'Other': [] }, contributors: [] }];
}

// Split into commits + contributors
const parts = stdout.split('|||CONTRIBUTORS|||');
let commitsData = parts[0] || '';
let contributorsData = '';

if (parts.length > 1) {
  const dataParts = parts[1].split('|||DATA_CONTRIBUTORS|||');

  // commits that appear before the DATA_CONTRIBUTORS delimiter
  if (dataParts[0]) {
    commitsData += '\n' + dataParts[0].split(/\r?\n/).filter(line => /^[a-f0-9]{7,}/i.test(line)).join('\n');
  }

  if (dataParts.length > 1) {
    const flowsParts = dataParts[1].split('|||FLOWS_CONTRIBUTORS|||');

    // commits between DATA_CONTRIBUTORS and FLOWS_CONTRIBUTORS
    if (flowsParts[0]) {
      commitsData += '\n' + flowsParts[0].split(/\r?\n/).filter(line => /^[a-f0-9]{7,}/i.test(line)).join('\n');
    }

    if (flowsParts.length > 1) {
      const jpaiParts = flowsParts[1].split('|||JPAI_CONTRIBUTORS|||');

      // commits between FLOWS_CONTRIBUTORS and JPAI_CONTRIBUTORS
      if (jpaiParts[0]) {
        commitsData += '\n' + jpaiParts[0].split(/\r?\n/).filter(line => /^[a-f0-9]{7,}/i.test(line)).join('\n');
      }

      // combine contributors (Tardigrade + Data + Flows + JP-AI)
      contributorsData = dataParts[0] + '\n' + (jpaiParts[0] || '') + '\n' + (jpaiParts[1] || '');
    } else {
      // combine contributors (Tardigrade + Data + Flows)
      contributorsData = dataParts[0] + '\n' + (flowsParts[1] || '');
    }
  } else {
    // only Tardigrade + Data contributors
    contributorsData = dataParts[0] + '\n' + (dataParts[1] || '');
  }
}

// Process commits
const lines = commitsData.split(/\r?\n/).filter(line => line.trim().length > 0);

let allCommits = [];
lines.forEach((line) => {
  const trimmed = line.trim();
  if (/^[a-f0-9]{7,}/i.test(trimmed)) {
    allCommits.push(trimmed);
  }
});

const categories = {
  'Added': [],
  'Fixed': [],
  'Changed': [],
  'Removed': [],
  'Other': []
};

allCommits.forEach((commit) => {
  const match = commit.match(/^[a-f0-9]{7,}\s+([A-Za-z]+):/);
  let category = match ? match[1].toLowerCase() : null;
  switch (category) {
    case 'added':
      categories['Added'].push(commit);
      break;
    case 'fixed':
      categories['Fixed'].push(commit);
      break;
    case 'changed':
    case 'updated':
    case 'modified':
      categories['Changed'].push(commit);
      break;
    case 'removed':
    case 'deleted':
      categories['Removed'].push(commit);
      break;
    default:
      categories['Other'].push(commit);
  }
});

// Process contributors
const contributorLines = contributorsData.split(/\r?\n/).filter(line => {
  const trimmed = line.trim();
  return trimmed.length > 0 && 
         !trimmed.includes('|||') && // Filter out our delimiters
         !/^[a-f0-9]{7,}/i.test(trimmed) && // Filter out commit hashes
         !trimmed.includes('@') && // Filter out email addresses
         !/^\d+$/.test(trimmed) && // Filter out pure numbers
         trimmed.length > 1 && trimmed.length < 50; // Reasonable name length
});

// Remove duplicates and filter
const uniqueContributors = [...new Set(contributorLines)].filter(name => {
  const lowerName = name.toLowerCase();
  return !lowerName.includes('bot') && 
         !lowerName.includes('action') &&
         !lowerName.includes('github') &&
         /^[A-Za-z\s\-\.]+$/.test(name); // Only letters, spaces, hyphens, dots
});

console.log('Contributors found:', uniqueContributors);

return [{ categories, contributors: uniqueContributors }];

const data = $input.first();
console.log('Generate .tex file node - Input received:', JSON.stringify(data, null, 2));

// Check if data exists and handle n8n data structure
if (!data) {
  throw new Error('No input data received in Generate .tex file node');
}

let actualData = data;

// Handle AI Agent output structure: data.json.message.content contains the actual data
if (data.json && data.json.message && data.json.message.content) {
  actualData = data.json.message.content;
} else if (data.json && !data.categories) {
  actualData = data.json;
} else if (data.message && data.message.content) {
  actualData = data.message.content;
}

if (!actualData.categories) {
  throw new Error(
    'Categories data missing. Available keys: ' + Object.keys(data).join(', ') +
    (data.json ? '. Keys in json: ' + Object.keys(data.json).join(', ') : '') +
    (data.json && data.json.message ? '. Keys in message: ' + Object.keys(data.json.message).join(', ') : '')
  );
}

const { categories, contributors, dateRange } = actualData;

// Handle LLM response - extract summaries
let generatedSummaries = [];
if (actualData.summaries && Array.isArray(actualData.summaries)) {
  generatedSummaries = actualData.summaries;
} else if (actualData.summaryOverview) {
  // Legacy format
  generatedSummaries = actualData.summaryOverview.tasks || [];
} else if (actualData.response || actualData.text || actualData.output) {
  // LLM node output format
  const llmResponse = actualData.response || actualData.text || actualData.output;
  const responseText = typeof llmResponse === 'string' ? llmResponse : JSON.stringify(llmResponse);
  const lines = responseText.split(' \n').filter(line => line.trim().includes('\\item '));
  generatedSummaries = lines.map(line => line.replace(/^\s*\\item\s*/, '').trim());
}

// Always build fallback summaries
const totalCommits = Object.values(categories).flat().length;
const categoryStats = Object.keys(categories)
  .map(cat => `${categories[cat].length} ${cat.toLowerCase()}`)
  .filter(stat => !stat.startsWith('0')).join(', ');

const fallbackTasks = [

  `\\textbf{Completed:} ${totalCommits} Development tasks: ${categoryStats}`,
  `\\textbf{Completed:} Reviewed codebase changes and pull requests for the past week`,
  `\\textbf{Completed:} Maintenance of code quality through testing and validation processes`,
  `\\textbf{Completed:} Documentation updates and resolved technical debt items`,
  `\\textbf{Completed:} Feature improvements and bug fixes for better user experience`
];

// Merge both: generated + fallback
const allTasks = [...fallbackTasks, ...generatedSummaries];

const summaryOverview = {
  tasks: allTasks,
  latexFormatted: allTasks.map(task => {
    // Ensure consistent bold formatting
    const formattedTask = task.replace(/^Completed:/, '\\textbf{Completed:}');
    return '  \\item  ' + formattedTask;
  }).join(' \n'),
  totalCommits: totalCommits
};

// Generate LaTeX (.tex) content for LaTeX editors
const generateTexContent = () => {
  // Format commits for LaTeX
  const formatCommitsForLatex = (categories) => {
    let latexCommits = '';
    Object.keys(categories).forEach(category => {
      if (categories[category] && categories[category].length > 0) {
        categories[category].forEach(commit => {
          const parts = commit.split(' ');
          const hash = parts[0];
          const message = parts.slice(1).join(' ');

          // Escape LaTeX special characters properly
          const escapedMessage = message
            .replace(/\\/g, '\\textbackslash{}')
            .replace(/_/g, '\\_')
            .replace(/\$/g, '\\$')
            .replace(/%/g, '\\%')
            .replace(/&/g, '\\&')
            .replace(/\^/g, '\\textasciicircum{}')
            .replace(/~/g, '\\textasciitilde{}')
            .replace(/\{/g, '\\{')
            .replace(/\}/g, '\\}');

          latexCommits += `- ${escapedMessage} | ${hash} \n`;
        });
      }
    });
    return latexCommits;
  };

  const formattedCommits = formatCommitsForLatex(categories);
  const escapedContributors = (contributors || 'Sergiu, Ivan')
    .replace(/_/g, '\\_')
    .replace(/&/g, '\\&');

  // Use the LaTeX formatted summary from summaryOverview
  const summaryContent = summaryOverview.latexFormatted || 
    summaryOverview.tasks.map(task => '  \\item ' + task).join(' \n');

  const texContent = '\\documentclass[12pt]{article} \n' +
    '\\usepackage{geometry} \n' +
    '\\geometry{margin=0.6in} \n' +
    '\\usepackage{enumitem} \n' +
    '\\usepackage{titlesec} \n' +
    '\\usepackage{hyperref} \n' +
    '\\usepackage{fancyvrb} \n' +
    '\\usepackage{titling} \n' +
    '\\usepackage{fancyhdr} \n \n' +
    '\\setlength{\\droptitle}{-1cm} \n' +
    '\\setlength{\\footskip}{7mm} \n \n' +
    '\\title{\\textbf{Weekly Progress Report}} \n' +
    '\\author{Contributors: ' + escapedContributors + '} \n' +
    '\\begin{document} \n' +
    '\\maketitle \n \n' +
    '\\noindent\\vspace{-1.5cm}\\section*{※ Project Priorities} \n' +
    '\\begin{itemize} \n' +
    '  \\item \\textbf{Applications} (Terminal, Janes, Training Platform) \n' +
    '  \\item \\textbf{Shared Libs} (Backend Functional Capabilities) \n' +
    '  \\item \\textbf{Maintenance} (Tests, Logs, Automation, CI/CD, Validation, Infra, Configs, Docs, AI, etc.) \n' +
    '\\end{itemize} \n \n' +
    '\\noindent\\section*{※ Summary Overview} \n' +
    '\\begin{itemize} \n' +
    summaryContent + ' \n' +
    '\\end{itemize} \n \n' +
    '\\noindent\\section*{※ Development Tasks Completed (Weekly Changelog Report)} \n' +
    '\\vspace{0.3cm} \n' +
    '\\begin{Verbatim}[fontsize=\\fontsize{10}{20}\\selectfont] \n' +
    formattedCommits +
    '\\end{Verbatim} \n \n' +
    '\\end{document}';

  return texContent;
};

const texFileContent = generateTexContent();

console.log('LaTeX .tex file generated successfully');
console.log('Total commits processed:', Object.values(categories).flat().length);
console.log('Summary tasks generated:', summaryOverview.tasks.length);

// Return in n8n expected format with tex content as main output
return {
  texContent: texFileContent
};

// Get first input item
const data = $input.first();

if (!data || !data.json || !data.json.texContent) {
  throw new Error('No texContent found in input item.');
}

// Only replace literal backslash + n that are followed by a space or end-of-string
// This prevents messing with LaTeX commands
const cleanTex = data.json.texContent.replace(/\\n(?=\s|$)/g, "\n");

// Return both viewable JSON and downloadable binary
return {
  json: {
    texContent: cleanTex,
    fileName: "log.txt",
    mimeType: "text/plain",
    size: cleanTex.length
  },
  binary: {
    data: {
      data: Buffer.from(cleanTex, "utf8").toString("base64"),
      mimeType: "text/plain",
      fileName: "log.txt"
    }
  }
};
